import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Papa from 'papaparse';
import MvBanner from './MvBanner';
import '../css/MvchoPage.css';
import logoImage from '../logo.png';
import moviesCSV from './movie.csv'; // CSV 파일을 import

function MvchoPage() {

  const genreMapping = {
    '장르 전체': 'All',
    '액션': 'Action',
    '모험': 'Adventure',
    '애니메이션': 'Animation',
    '코미디': 'Comedy',
    '범죄': 'Crime',
    '다큐멘터리': 'Documentary',
    '드라마': 'Drama',
    '가족': 'Family',
    '판타지': 'Fantasy',
    '역사': 'History',
    '공포': 'Horror',
    '음악': 'Music',
    '미스터리': 'Mystery',
    '로맨스': 'Romance',
    'SF': 'Sci-Fi',
    'TV 영화': 'TV Movie',
    '스릴러': 'Thriller',
    '전쟁': 'War',
    '서부': 'Western'
  };
  const genres = Object.keys(genreMapping);
  const [selectedGenre, setSelectedGenre] = useState('장르 전체');
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchMovies = async () => {
      fetch(moviesCSV)
        .then(response => response.text())
        .then(csvText => {
          Papa.parse(csvText, {
            header: true,
            skipEmptyLines: true,
            complete: (result) => {
              const processedData = result.data.map(movie => ({
                ...movie,
                flatrate: movie.flatrate ? movie.flatrate.split(', ') : []
              }));
              setMovies(processedData);
              setLoading(false);
            },
          });
        })
        .catch(error => {
          console.error('Error fetching CSV file:', error);
          setLoading(false);
        });
    };

    fetchMovies();
  }, []);

  const filteredMovies = selectedGenre === '장르 전체' ? movies : movies.filter(movie =>
    movie.genre.split(', ').includes(genreMapping[selectedGenre])
  );

  const banners = filteredMovies.map((movie, index) => (
    <MvBanner
      key={index}
      title={movie.title}
      poster={movie.poster_path}
      flatrate={movie.flatrate}
      rating={Math.round(movie.vote_average / 2)}
    />
  ));

  return (
    <div className="MvchoPage">
      <img src={logoImage} alt="Logo" className="logo"/>
      <div className="mainText">재미있게 봤거나 눈길이 가는 영화들을 평가해주세요.</div>
      <div className="subText">찜한 영화들을 바탕으로 MOVIELY가 취향저격 영화들을 추천해 드려요.</div>
      <div className="stickyTop">
        <Link to="/recommendations">
          <button className="recommendButton">영화 추천 받기 &gt;</button>
        </Link>
        <div className="genreButtons">
          {genres.map((genre) => (
            <button key={genre} className="genreButton1" onClick={() => setSelectedGenre(genre)}>{genre}</button>
          ))}
        </div>
      </div>
      <div className="bannerGrid">
        {loading ? <div className="loading">로딩 중...</div> : (banners.length > 0 ? banners : <div className="noMovies">선택하신 장르의 영화가 없습니다.</div>)}
      </div>
    </div>
  );
}

export default MvchoPage;
