import React from 'react';
import { useNavigate, Link } from 'react-router-dom';
import '../css/LogSignPage.css';
import logoImage from '../logo.png';

function LogSignPage() {
  const navigate = useNavigate();

  const handleGoogleLogin = async () => {
    try {
      // 구글 로그인 로직 (구글 로그인 API와 연동)
      const user = { id: 'googleUserId' }; // 가정: 구글 로그인 후 받은 사용자 정보

      // 사용자 정보 확인 API 호출
      const response = await fetch('http://43.203.39.119:8080/api/check-user', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ userId: user.id }),
      });

      if (response.ok) {
        const data = await response.json();
        if (data.isNewUser) {
          navigate('/add');
        } else {
          navigate('/movie-select');
        }
      } else {
        alert('사용자 확인에 실패했습니다.');
      }
    } catch (error) {
      console.error('Google login failed:', error);
      alert('Google 로그인에 실패했습니다.');
    }
  };

  return (
    <div className="logSignPage">
      <Link to="/" className="logSign-logo">
        <img src={logoImage} alt="Logo" />
      </Link>
      <div className="logSignBox">
        <h2>로그인 및 회원가입</h2>
        <p>소셜 로그인 및 회원가입으로<br />MOVIELY의 모든 서비스를 이용하실 수 있습니다.</p>
        <button className="googleLoginButton" onClick={handleGoogleLogin}>
          Google로 시작하기
        </button>
      </div>
    </div>
  );
}

export default LogSignPage;
