import React, { useState } from 'react';
import '../css/Mvbanner.css'; // CSS 파일 임포트
import Popcho from '../pages/Popcho'; // Popcho 모달 컴포넌트 임포트

// 로고 이미지를 import 합니다.
import watchaLogo from '../watcha.png';
import netflixLogo from '../netflix.png';
import disneyPlusLogo from '../disneyplus.png';

// flatrate 값을 로고 이미지로 매핑합니다.
const flatrateLogos = {
  'Watcha': watchaLogo,
  'Netflix': netflixLogo,
  'Disney Plus': disneyPlusLogo,
};

const MvBanner = ({ title, poster, flatrate }) => {
  const [rating, setRating] = useState(0); // 별점 상태 초기화
  const [showModal, setShowModal] = useState(false); // 모달창 보임 상태 초기화

  // 별점을 클릭할 때 실행되는 함수
  const handleStarClick = (index) => {
    // 별점이 클릭된 경우 현재 별점을 조정합니다.
    setRating(rating === index + 1 ? rating - 1 : index + 1);
  };

  // '추가하기' 버튼 클릭 핸들러
  const handleAddClick = () => {
    // 모달창을 보여줍니다.
    setShowModal(true);
  };

  // 모달을 닫는 함수
  const handleCloseModal = () => {
    // 모달창을 숨깁니다.
    setShowModal(false);
  };

  // 모달 저장 핸들러
  const handleSaveModal = (option) => {
    // 저장 옵션을 콘솔에 기록하고 모달을 닫습니다.
    console.log("저장 옵션:", option);
    setShowModal(false);
  };

  // flatrate 값을 통해 로고 이미지 경로를 얻습니다.
  const logoImage = flatrateLogos[flatrate];

  return (
    <div className="movie-banner" style={{ width: '154px', height: '273px' }}>
      <img src={poster} alt={title} className="movie-poster" style={{ width: '154px', height: '231px' }} />
      <div className="movie-info" style={{ padding: '0 10px', height: '42px' }}>
        {/* flatrate 값에 따른 조건부 로고 렌더링 */}
        {logoImage && (
          <img
            src={logoImage}
            alt={`${flatrate} 로고`}
            className="flatrate-logo"
            style={{ width: '20px', height: '20px' }}
          />
        )}
        <div className="movie-title" style={{ fontSize: '10px', color: 'white' }}>{title}</div>
        <div className="movie-rating">
          {/* 별점을 렌더링하는 부분 */}
          {[...Array(5)].map((_, index) => (
            <span
              key={index}
              className={`star ${rating > index ? 'filled' : ''}`}
              style={{ fontSize: '14px' }}
              onClick={() => handleStarClick(index)}
            >
              ★
            </span>
          ))}
        </div>
        <button
          onClick={handleAddClick}
          className="add-button"
          style={{ fontSize: '15px' }}
        >
          +
        </button>
      </div>
      {/* 모달 컴포넌트 렌더링 */}
      {showModal && <Popcho onClose={handleCloseModal} onSave={handleSaveModal} />}
    </div>
  );
};

export default MvBanner;
