import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import interactionPlugin from '@fullcalendar/interaction'; // 상호작용 플러그인 임포트
import Popcal from './Popcal'; // Popcal 컴포넌트 임포트
import '../css/MycalPage.css';
import logoImage from '../logo.png';

function MycalPage() {
  const [isPopupOpen, setIsPopupOpen] = useState(false); // 팝업창 상태

  // 날짜 클릭 핸들러
  const handleDateClick = ({ dateStr }) => {
    // 클릭된 날짜로 상태를 설정하고 팝업창을 엽니다.
    setIsPopupOpen(true);
  };

  // 기록하기 버튼 클릭 핸들러
  const handleRecordButtonClick = () => {
    // 팝업창을 엽니다.
    setIsPopupOpen(true);
  };

  // 팝업창 닫기 핸들러
  const handleClosePopup = () => {
    setIsPopupOpen(false);
  };

  return (
    <div className="mycalPage">
      <img src={logoImage} alt="Logo" className="myPageLogo" />
      <div className="myPageTitle">마이페이지</div>
      <div className="navButtons">
        <Link to="/my/watched" className="navButton">이미 본 영화</Link>
        <Link to="/my/wishlist" className="navButton">보고싶은 영화</Link>
        <Link to="/my/calendar" className="navButton active">MOVIELY 캘린더</Link>
      </div>
      <button
        onClick={handleRecordButtonClick}
        className="recordButton"
      >
        영화 관람 기록하기
      </button>
      <div className="calendar-container">
        <FullCalendar
          plugins={[dayGridPlugin, interactionPlugin]}
          initialView="dayGridMonth"
          dateClick={handleDateClick}
          height="470px"
        />
      </div>
      {isPopupOpen && (
        <Popcal
          isOpen={isPopupOpen}
          onClose={handleClosePopup}
          // onSave 함수나 필요한 props는 Popcal 컴포넌트 내에서 정의하거나 전달됩니다.
        />
      )}
    </div>
  );
}

export default MycalPage;
