import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import LandingPage from './pages/LandingPage';
import LoginPage from './pages/LoginPage';
import SigninPage from './pages/SigninPage';
import MvchoPage from './pages/MvchoPage';
import RecomPage from './pages/RecomPage';
import MvdetailPage from './pages/MvdetailPage';
import MyalrPage from './pages/MyalrPage';
import MywishPage from './pages/MywishPage';
import MycalPage from './pages/MycalPage';
import MvsrchPage from './pages/MvsrchPage';

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      // 여기에서 토큰을 해독하거나 사용자 정보를 가져올 수 있습니다.
      const userInfo = JSON.parse(localStorage.getItem('user'));
      setUser(userInfo);
      setIsAuthenticated(true);
    }
  }, []);

  const handleLogin = (token, userInfo) => {
    localStorage.setItem('token', token);
    localStorage.setItem('user', JSON.stringify(userInfo));
    setUser(userInfo);
    setIsAuthenticated(true);
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
    setIsAuthenticated(false);
  };

  return (
    <Router>
      <div>
        <Routes>
          <Route path="/" element={<LandingPage />} />
          <Route path="/login" element={<LoginPage onLogin={handleLogin} />} />
          <Route path="/signin" element={<SigninPage />} />
          <Route path="/movie-select" element={isAuthenticated ? <MvchoPage user={user} /> : <Navigate to="/login" />} />
          <Route path="/recommendations" element={isAuthenticated ? <RecomPage user={user} /> : <Navigate to="/login" />} />
          <Route path="/movie/:id" element={isAuthenticated ? <MvdetailPage user={user} /> : <Navigate to="/login" />} />
          <Route path="/my/watched" element={isAuthenticated ? <MyalrPage user={user} /> : <Navigate to="/login" />} />
          <Route path="/my/wishlist" element={isAuthenticated ? <MywishPage user={user} /> : <Navigate to="/login" />} />
          <Route path="/my/calendar" element={isAuthenticated ? <MycalPage user={user} /> : <Navigate to="/login" />} />
          <Route path="/movie-search" element={isAuthenticated ? <MvsrchPage user={user} /> : <Navigate to="/login" />} />
        </Routes>
        {isAuthenticated && <button onClick={handleLogout}>로그아웃</button>}
      </div>
    </Router>
  );
};

export default App;
