// MvchoPage.js
import React from 'react';
import { Link } from 'react-router-dom';
import MvBanner from './MvBanner';
import '../css/MvchoPage.css';
import logoImage from '../logo.png'; // 페이지 로고 이미지
import { mockMovies } from './Mockmv'; // Mock 데이터 가져오기

function MvchoPage() {
  const genres = ['장르 전체', '스릴러', 'SF', '미스터리', '애니메이션', '로맨스', '판타지', '다큐멘터리', '코미디', '공포'];

  const mockBanners = mockMovies.map((movie) => (
    <MvBanner
      key={movie.movie_id}
      title={movie.title}
      poster={movie.poster_path}
      flatrate={movie.flatrate} // flatrate 문자열을 직접 전달
      rating={Math.round(movie.vote_average / 2)}
    />
  ));

  return (
    <div className="MvchoPage">
      <img src={logoImage} alt="Logo" className="logo" />
      <div className="mainText">
        재미있게 봤거나 눈길이 가는 영화들을 평가해주세요.
      </div>
      <div className="subText">
        찜한 영화들을 바탕으로 MOVIELY가 취향저격 영화들을 추천해 드려요.
      </div>
      <div className="stickyTop">
        <Link to="/recommendations">
          <button className="recommendButton">영화 추천 받기 &gt;</button>
        </Link>
        <div className="genreButtons">
          {genres.map((genre) => (
            <button key={genre} className="genreButton">{genre}</button>
          ))}
        </div>
      </div>
      <div className="bannerGrid">
        {mockBanners}
      </div>
    </div>
  );
}

export default MvchoPage;
