import React from 'react';
import { Link } from 'react-router-dom';
import '../css/SigninPage.css'; 
import logoImage from '../logo.png'; 

function SigninPage() {
  const ageOptions = [];
  for (let i = 15; i <= 100; i++) {
    ageOptions.push(<option key={i} value={i}>{i}</option>);
  }

  return (
    <div className="signinPage">
      <img src={logoImage} alt="Logo" className="logo"/>
      <div className="signinBox">
        <h2>회원가입</h2>
        <div className="inputGroup">
  <label htmlFor="email">이메일 입력</label>
  <input type="email" id="email" name="user_email" placeholder="이메일을 입력하세요"/>
</div>
<div className="inputGroup">
  <label htmlFor="password">비밀번호 입력</label>
  <input type="password" id="password" name="user_pwd" placeholder="비밀번호를 입력하세요"/>
</div>
<div className="selectGroup">
  <div>
    <label htmlFor="gender">성별</label>
    <select id="gender" name="gender" className="genderSelect">
      <option value="" disabled selected>성별을 선택해주세요</option>
      <option value="female">여성</option>
      <option value="male">남성</option>
    </select>
  </div>
  <div>
    <label htmlFor="age">나이</label>
    <select id="age" name="age" className="ageSelect">
      <option value="" disabled selected>나이를 선택해주세요</option>
      {ageOptions}
    </select>
  </div>
</div>
        <Link to="/movie-select" className="signinButtonLink">
  <button className="signinButton">가입하기</button>
</Link>

        <Link to="/login" className="loginLink">이미 MOVIELY 계정이 있으신가요?</Link>
      </div>
    </div>
  );

}

export default SigninPage;
