import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import MvBanner from './MvBanner'; // 가정한 경로에 맞게 수정해 주세요.
import '../css/RecomPage.css'; // CSS 파일 임포트
import logoImage from '../logo.png'; // 로고 이미지 경로 가정

function RecomPage() {
  const navigate = useNavigate();
  const [searchTerm, setSearchTerm] = useState('');
  const [activeTab, setActiveTab] = useState('recommendations');

  const handleSearchClick = () => {
    navigate('/movie-search', { state: { searchTerm } });
  };

  return (
    <div className="RecomPage">
      <img src={logoImage} alt="Logo" className="logo" />
      <div className="searchAndMyPageContainer">
        <div className="searchContainer">
          <input
            type="text"
            className="searchInput"
            placeholder="검색"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <button className="searchButton" onClick={handleSearchClick}>
            검색하기
          </button>
        </div>
        <button className="myPageButton" onClick={() => navigate('/my/watched')}>
          마이페이지
        </button>
      </div>
      <div className="tabButtons">
        <button
          className={`tabButton ${activeTab === 'recommendations' ? 'active' : ''}`}
          onClick={() => setActiveTab('recommendations')}
        >
          내 취향 영화 추천받기
        </button>
        <button
          className={`tabButton ${activeTab === 'platforms' ? 'active' : ''}`}
          onClick={() => setActiveTab('platforms')}
        >
          내 맞춤 플랫폼 찾아보기
        </button>
      </div>
      <div className="tabContent">
        {activeTab === 'recommendations' && (
          <MvBanner title="Inception" poster="../inception.png" /> // 이 부분은 예시입니다.
        )}
        {
  activeTab === 'platforms' && (
    <div>
      {/* 내 맞춤 플랫폼 찾아보기 탭 내용이 들어갈 곳 */}
    </div>
  )
}
      </div>
    </div>
  );
}

export default RecomPage;
