import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Papa from 'papaparse';
import '../css/MvdetailPage.css';
import logoImage from '../logo.png';
import moviesCSV from '../movie.csv';

import watchaLogo from '../watcha.png';
import netflixLogo from '../netflix.png';
import disneyPlusLogo from '../disneyplus.png';
import wavveLogo from '../wavve.png';

const flatrateLogos = {
  'disney plus': disneyPlusLogo,
  'netflix': netflixLogo,
  'watcha': watchaLogo,
  'wavve': wavveLogo
};

const MvdetailPage = () => {
  const { id } = useParams();
  const [movie, setMovie] = useState(null);

  useEffect(() => {
    Papa.parse(moviesCSV, {
      download: true,
      header: true,
      complete: (result) => {
        const movies = result.data;
        const movie = movies.find(movie => movie.id === id); // id가 문자열이므로 일치하는지 확인
        setMovie(movie);
      },
    });
  }, [id]);

  if (!movie) return <div>Loading...</div>;

  const flatrateServices = movie.flatrate ? movie.flatrate.split(', ') : [];

  return (
    <div className="movie-detail-page">
      <header>
        <img src={logoImage} alt="Logo" className="logo" />
        <button className="mypage-button">마이페이지</button>
        <input type="text" className="search-bar" placeholder="검색" />
      </header>
      <div className="movie-info-container">
        <div className="left-column">
          <h1 className="movie-title">{movie.title}</h1>
          <div className="rating-and-add">
            <span className="rating">★★★★★</span>
            <button className="add-button">+</button>
          </div>
          <p className="movie-details">{movie.release_date} · {movie.genre} · {movie.country}</p>
          <p className="movie-runtime">{movie.runtime}분</p>
          <p className="movie-cast">출연 : {movie.cast}</p>
        </div>
        <div className="right-column">
          <img src={movie.poster_path} alt={movie.title} className="movie-poster" />
        </div>
      </div>
      <div className="movie-description">
        <p>{movie.overview}</p>
      </div>
      <div className="ott-buttons-container">
        <h2 className="ott-title">바로가기</h2>
        {flatrateServices.map(service => (
          <button key={service} className={`ott-button ${service}`}>
            <img src={flatrateLogos[service]} alt={service} className="ott-logo" />
            {service.charAt(0).toUpperCase() + service.slice(1)}
          </button>
        ))}
      </div>
    </div>
  );
};

export default MvdetailPage;
