import React from 'react';
import '../css/Mvbanner.css'; // CSS 파일 임포트

const Mvbanner = ({ title, poster, rating, onAdd }) => {
  // rating은 0에서 5 사이의 값으로 각 별을 채우기 위해 사용됩니다.

  return (
    <div className="movie-banner">
      <img src={poster} alt={title} className="movie-poster" />
      <div className="movie-info">
        <div className="movie-title">{title}</div>
        <div className="movie-rating">
          {[...Array(5)].map((_, index) => (
            <span key={index} className={`star ${rating > index ? 'filled' : ''}`}>
              ★
            </span>
          ))}
        </div>
        <button onClick={onAdd} className="add-button">+</button>
      </div>
    </div>
  );
};

export default Mvbanner;
