// React와 필요한 assets 및 스타일 import
import React, { useState } from 'react';
import '../css/Mvbanner.css';
import Popcho from '../pages/Popcho';

// 플랫폼 로고 이미지들을 import합니다.
import watchaLogo from '../watcha.png';
import netflixLogo from '../netflix.png';
import disneyPlusLogo from '../disneyplus.png';

// 플랫폼에 맞는 로고 매핑
const platformLogos = {
  'Watcha': watchaLogo,
  'Netflix': netflixLogo,
  'Disney Plus': disneyPlusLogo,
};

// MvBanner 컴포넌트 정의
const MvBanner = ({ title, poster, flatrate }) => {
  const [rating, setRating] = useState(0); // 별점 상태
  const [showModal, setShowModal] = useState(false); // 모달창 상태

  // 별점을 설정하는 함수
  const handleStarClick = (index) => {
    setRating(rating === index + 1 ? rating - 1 : index + 1);
  };

  // '추가하기' 버튼 클릭 핸들러
  const handleAddClick = () => {
    setShowModal(true);
  };

  // 모달을 닫는 함수
  const handleCloseModal = () => {
    setShowModal(false);
  };

  // 모달 저장 핸들러
  const handleSaveModal = (option) => {
    console.log("저장 옵션:", option);
    setShowModal(false);
  };

  // flatrate 값에 따라 해당 로고 이미지를 결정합니다.
  const logoImage = platformLogos[flatrate];

  // MvBanner JSX 렌더링
  return (
    <div className="movie-banner">
      <img src={poster} alt={title} className="movie-poster" />
      <div className="movie-info">
        {logoImage && (
          <img
            src={logoImage}
            alt={`${flatrate} 로고`}
            className="platform-logo"
            style={{ width: '20px', height: '20px' }} // 로고 사이즈 설정
          />
        )}
        <div className="movie-title">{title}</div>
        <div className="movie-rating">
          {[...Array(5)].map((_, index) => (
            <span key={index}
              className={`star ${rating > index ? 'filled' : ''}`}
              onClick={() => handleStarClick(index)}>★</span>
          ))}
        </div>
        <button onClick={handleAddClick} className="add-button">+</button>
      </div>
      {showModal && <Popcho onClose={handleCloseModal} onSave={handleSaveModal} />}
    </div>
  );
};

export default MvBanner;
