import React, { useState } from 'react';
import '../css/RecomPage.css'; // CSS 파일 임포트
import MvBanner from './MvBanner'; // MvBanner 컴포넌트 임포트
import mockMovies from './Mockmv'; // 가정한 목업 데이터 파일 경로

function RecomPage() {
  const [activeTab, setActiveTab] = useState('movies'); // 초기 활성화 탭 설정

  return (
    <div className="RecomPage">
      {/* 탭 버튼 */}
      <div className="tab-buttons">
        <button
          onClick={() => setActiveTab('movies')}
          className={activeTab === 'movies' ? 'active' : ''}
        >
          내 취향 영화들 추천받기
        </button>
        <button
          onClick={() => setActiveTab('platforms')}
          className={activeTab === 'platforms' ? 'active' : ''}
        >
          내 맞춤 플랫폼 찾아보기
        </button>
      </div>

      {/* 탭 내용 */}
      <div className="tab-content">
        {activeTab === 'movies' && (
          <div className="movies-tab">
            {/* '내 취향 영화들 추천받기' 탭 내용 */}
            <MvBanner title={mockMovies[0].title} poster={mockMovies[0].poster_path} />
            {/* 추가적으로 더 많은 영화 배너를 표시할 수 있습니다. */}
          </div>
        )}
        {activeTab === 'platforms' && (
          <div className="platforms-tab">
            {/* '내 맞춤 플랫폼 찾아보기' 탭 내용은 비워두었습니다. */}
          </div>
        )}
      </div>
    </div>
  );
}

export default RecomPage;
