// MycalPage.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import interactionPlugin from '@fullcalendar/interaction'; // for dateClick functionality
import Popcal from './Popcal'; // custom modal component for movie data entry
import '../css/MycalPage.css';
import logoImage from '../logo.png';

function MycalPage() {
  const [isPopupOpen, setIsPopupOpen] = useState(false); // to toggle the modal display
  const [selectedDate, setSelectedDate] = useState(null); // to store the clicked date
  const [events, setEvents] = useState([]); // to store the events (movie records)

  // Handles date click on the calendar
  const handleDateClick = ({ dateStr }) => {
    setSelectedDate(dateStr); // store the clicked date
    setIsPopupOpen(true); // open the modal
  };

  // Handles the 'Record Movie' button click
  const handleRecordButtonClick = () => {
    setIsPopupOpen(true); // open the modal
  };

  // Closes the modal
  const handleClosePopup = () => {
    setIsPopupOpen(false);
  };

  // Saves the movie data and adds it to the events
  const handleSaveMovieData = (movieData) => {
    // Add the new event (movie record) to the existing events
    setEvents([...events, {
      title: movieData.movie_title,
      start: selectedDate,
      allDay: true
    }]);
    setIsPopupOpen(false); // close the modal after saving
  };

  return (
    <div className="mycalPage">
      <img src={logoImage} alt="Logo" className="myPageLogo" />
      <div className="myPageTitle">마이페이지</div>
      <div className="navButtons">
        <Link to="/my/watched" className="navButton">이미 본 영화</Link>
        <Link to="/my/wishlist" className="navButton">보고싶은 영화</Link>
        <Link to="/my/calendar" className="navButton active">MOVIELY 캘린더</Link>
      </div>
      <button
        onClick={handleRecordButtonClick}
        className="recordButton"
      >
        영화 관람 기록하기
      </button>
      <div className="calendar-container">
        <FullCalendar
          plugins={[dayGridPlugin, interactionPlugin]}
          initialView="dayGridMonth"
          dateClick={handleDateClick}
          height="470px"
          events={events} // pass the events to the calendar
        />
      </div>
      {isPopupOpen && (
        <Popcal
          isOpen={isPopupOpen}
          onClose={handleClosePopup}
          onSave={handleSaveMovieData} // pass the save handler to the modal
          user={{}} // pass the user if needed
          initialData={{}} // pass initial data if editing existing record
        />
      )}
    </div>
  );
}

export default MycalPage;
