import React from 'react';
import { useNavigate, Link } from 'react-router-dom';
import '../css/LogSignPage.css';
import logoImage from '../logo.png';

function LogSignPage() {
  const navigate = useNavigate();

  const handleGoogleLogin = async () => {
    // 구글 OAuth2 클라이언트 ID
    const clientId = 'YOUR_GOOGLE_CLIENT_ID';
    const redirectUri = 'YOUR_REDIRECT_URI'; // e.g., http://localhost:3000/oauth2/callback

    // Google OAuth2 로그인 URL
    const googleLoginUrl = `https://accounts.google.com/o/oauth2/auth?response_type=token&client_id=${clientId}&redirect_uri=${redirectUri}&scope=profile email`;

    // 구글 로그인 창 열기
    window.location.href = googleLoginUrl;
  };

  return (
    <div className="logSignPage">
      <Link to="/" className="logSign-logo">
        <img src={logoImage} alt="Logo" />
      </Link>
      <div className="logSignBox">
        <h2>로그인 및 회원가입</h2>
        <p>소셜 로그인 및 회원가입으로<br />MOVIELY의 모든 서비스를 이용하실 수 있습니다.</p>
        <button className="googleLoginButton" onClick={handleGoogleLogin}>
          Google로 시작하기
        </button>
      </div>
    </div>
  );
}

export default LogSignPage;
