// Popcal.js
import React, { useState, useEffect } from 'react';
import '../css/Popcal.css';

const Popcal = ({ isOpen, onClose, onSave, initialData }) => {
  const [movieData, setMovieData] = useState({
    watch_date: '',
    movie_title: '',
    movie_content: ''
  });

  useEffect(() => {
    // Ensure all fields are correctly initialized from initialData
    if (initialData) {
      setMovieData({
        watch_date: initialData.watch_date || '',
        movie_title: initialData.movie_title || '',
        movie_content: initialData.movie_content || ''
      });
    }
  }, [initialData]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setMovieData(prev => ({ ...prev, [name]: value }));
  };

  const handleSave = () => {
    onSave(movieData);
    onClose();
  };

  if (!isOpen) return null;

  return (
    <div className="popcal-container">
      <div className="popcal">
        <h2>영화 관람 기록하기</h2>
        <label htmlFor="watch_date">관람 일자</label>
        <input
          id="watch_date"
          type="date"
          name="watch_date"
          value={movieData.watch_date}
          onChange={handleInputChange}
        />
        <label htmlFor="movie_title">영화 제목</label>
        <input
          id="movie_title"
          type="text"
          name="movie_title"
          value={movieData.movie_title}
          onChange={handleInputChange}
        />
        <label htmlFor="movie_content">감상 기록</label>
        <textarea
          id="movie_content"
          name="movie_content"
          value={movieData.movie_content}
          onChange={handleInputChange}
        />
        <div className="popcal-buttons">
          <button onClick={onClose}>닫기</button>
          <button onClick={handleSave}>저장하기</button>
        </div>
      </div>
    </div>
  );
};

export default Popcal;
