import React, { useState } from 'react';
import '../css/Popcal.css';

const Popcal = ({ isOpen, onClose, onSave, user, initialData }) => {
  // 초기 데이터 또는 기본값으로 상태를 설정
  const [movieData, setMovieData] = useState({
    watch_date: initialData?.watch_date || '',
    movie_title: initialData?.movie_title || '',
    movie_content: initialData?.movie_content || '',
    user_id: user?.id || '',
    created: initialData?.created || '',
    created_by: user?.name || ''
  });

  // 각 입력 필드를 업데이트하는 함수
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setMovieData(prevData => ({ ...prevData, [name]: value }));
  };

  // 저장 버튼 핸들러
  const handleSave = () => {
    // 여기에 백엔드에 데이터를 보내는 로직을 추가
    onSave(movieData);
  };

  if (!isOpen) return null;

  return (
    <div className="popcal-container">
      {/* 팝업 내용 */}
      <div className="popcal">
        <h2>영화 관람 기록하기</h2>
        <input
          type="date"
          name="watch_date"
          value={movieData.watch_date}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="movie_title"
          placeholder="영화 제목"
          value={movieData.movie_title}
          onChange={handleInputChange}
        />
        <textarea
          name="movie_content"
          placeholder="감상 기록"
          value={movieData.movie_content}
          onChange={handleInputChange}
        />
        <button onClick={handleSave}>닫기</button>
        <button onClick={onClose}>저장하기</button>
      </div>
    </div>
  );
};

export default Popcal;
