import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Papa from 'papaparse';
import moviesCSV from '../movie.csv';
import watchaLogo from '../watcha.png';
import netflixLogo from '../netflix.png';
import disneyPlusLogo from '../disneyplus.png';
import wavveLogo from '../wavve.png';
import Popcho from '../pages/Popcho';

const flatrateLogos = {
  'disney plus': disneyPlusLogo,
  'netflix': netflixLogo,
  'watcha': watchaLogo,
  'wavve': wavveLogo,
};

const MvdetailPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [movie, setMovie] = useState(null);
  const [loading, setLoading] = useState(true);
  const [rating, setRating] = useState(0);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    Papa.parse(moviesCSV, {
      download: true,
      header: true,
      complete: (result) => {
        console.log("Parsed movies:", result.data);
        const foundMovie = result.data.find(movie => parseInt(movie.movie_id, 10) === parseInt(id, 10));
        console.log("Found movie:", foundMovie);
        setMovie(foundMovie);
        setLoading(false);
      },
    });
  }, [id]);

  const handleStarClick = (index) => {
    setRating(index + 1);
  };

  const handleAddClick = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleSaveModal = (option) => {
    console.log("저장 옵션:", option);
    setShowModal(false);
  };

  const goToMyPage = () => {
    navigate('/my/watched');
  };

  if (loading) {
    return <div style={{ color: 'white', textAlign: 'center' }}>Loading...</div>;
  }

  if (!movie) {
    return <div style={{ color: 'white', textAlign: 'center' }}>Movie not found</div>;
  }

  const validFlatrate = typeof movie.flatrate === 'string' ? movie.flatrate.split(', ').map(service => service.trim().toLowerCase()).filter(Boolean) : [];

  const posterUrl = movie.poster_path ? `http://image.tmdb.org/t/p/w500${movie.poster_path}` : 'https://via.placeholder.com/154x231?text=No+Image';

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', width: '100%', color: 'white' }}>
      <header style={{ display: 'flex', justifyContent: 'space-between', width: '100%', padding: '20px 50px' }}>
        <img src="/path/to/logo.png" alt="Logo" style={{ width: '100px', marginLeft: 'auto', marginRight: 'auto' }} />
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'flex-end' }}>
          <button
            style={{
              width: '90px',
              height: '23px',
              borderRadius: '24px',
              fontSize: '12px',
              backgroundColor: 'white',
              color: 'black',
              cursor: 'pointer',
            }}
            onMouseOver={(e) => e.target.style.backgroundColor = '#FBE64D'}
            onMouseOut={(e) => e.target.style.backgroundColor = 'white'}
            onClick={goToMyPage}
          >마이페이지</button>
          <input
            type="text"
            style={{
              width: '200px',
              marginTop: '10px',
            }}
            placeholder="검색"
          />
        </div>
      </header>
      <div style={{ display: 'flex', width: '80%', justifyContent: 'center', marginTop: '20px' }}>
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'flex-start', marginRight: '20px' }}>
          <h1>{movie.title}</h1>
          <div style={{ display: 'flex', alignItems: 'center' }}>
            <span>
              {[...Array(5)].map((_, index) => (
                <span
                  key={index}
                  className={`star ${rating > index ? 'filled' : ''}`}
                  onClick={() => handleStarClick(index)}
                >
                  ★
                </span>
              ))}
            </span>
            <button onClick={handleAddClick} style={{ marginLeft: '10px', cursor: 'pointer' }}>+</button>
          </div>
          <p>{movie.release_date} | {movie.genre} | {movie.production_countries}</p>
          <p>{movie.run_time}분</p>
          <p>출연진: {movie.cast}</p>
        </div>
        <div>
          <img src={posterUrl} alt={movie.title} style={{ height: '370px' }} />
        </div>
      </div>
      <div style={{ width: '60%', marginTop: '20px' }}>
        <h2>개요</h2>
        <p>{movie.overview}</p>
      </div>
      <div style={{ width: '60%', marginTop: '20px' }}>
        <h2 style={{ textAlign: 'center', fontWeight: 'bold', fontSize: '20px' }}>바로가기</h2>
        {validFlatrate.map((platform, index) => (
          <button
            key={index}
            style={{
              width: '420px',
              height: '50px',
              margin: '10px 0',
              border: '1px solid white',
              borderRadius: '15px',
              backgroundColor: 'white',
              color: 'black',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              cursor: 'pointer',
            }}
            onMouseOver={(e) => e.target.style.backgroundColor = '#FBE64D'}
            onMouseOut={(e) => e.target.style.backgroundColor = 'white'}
          >
            <img src={flatrateLogos[platform]} alt={platform} style={{ height: '20px', marginRight: '10px' }} />
            <span style={{ fontSize: '15px', fontWeight: 'bold' }}>{platform}</span>
          </button>
        ))}
      </div>
      {showModal && <Popcho onClose={handleCloseModal} onSave={handleSaveModal} />}
    </div>
  );
};

export default MvdetailPage;
