// MvsrchPage.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../css/MvsrchPage.css'; // MvsrchPage의 CSS 파일을 임포트합니다.
import logoImage from '../logo.png'; // 로고 이미지 경로를 정확하게 지정하세요.

function MvsrchPage() {
  const navigate = useNavigate();
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearchClick = () => {
    // "검색하기" 버튼 클릭 시 로직
    navigate('/movie-search', { state: { searchTerm } });
  };

  return (
    <div className="MvsrchPage">
      <img src={logoImage} alt="Logo" className="logo"/>
      <button className="myPageButton" onClick={() => navigate('/my/watched')}>마이페이지</button>
      <div className="searchContainer">
        <input
          type="text"
          className="searchInput"
          placeholder="검색"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button className="searchButton" onClick={handleSearchClick}>
          검색하기
        </button>
      </div>
      {/* 여기에 MvsrchPage의 나머지 내용을 추가 */}
    </div>
  );
}

export default MvsrchPage;
