import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { mockMovies } from './Mockmv'; // 목업 데이터 경로 확인 필요
import '../css/LandingPage.css'; // CSS 파일 경로 확인 필요
import logoImage from '../logo.png'; // 로고 이미지 경로 확인 필요

function LandingPage() {
  const [randomPosters, setRandomPosters] = useState([]);

  // 컴포넌트가 마운트될 때 랜덤 포스터를 설정합니다.
  useEffect(() => {
    // 목업 데이터에서 랜덤한 영화 포스터 경로 3개를 추출합니다.
    setRandomPosters(mockMovies.sort(() => 0.5 - Math.random()).slice(0, 3));
  }, []);

  return (
    <div className="container">
      <img src={logoImage} alt="Logo" className="logo"/>
      <h1 className="heading">오늘 뭘 볼지 모르겠다고요?<br />내 취향에 꼭 맞는 OTT 컨텐츠를 찾아보세요!</h1>
      <div className="buttonContainer">
        <Link to="/login" className="button">로그인</Link>
        <Link to="/signin" className="button">회원가입</Link>
      </div>
      <div className="image-container">
        {randomPosters.map((poster, index) => (
          <div
            key={index}
            className="image"
            style={{ backgroundImage: `url(${poster.poster_path})`, backgroundSize: 'cover' }}
          />
        ))}
      </div>
    </div>
  );
}

export default LandingPage;
