import React from 'react';
import { Link } from 'react-router-dom';
import '../css/LoginPage.css'; // CSS 파일 임포트
import logoImage from '../logo.png'; // 로고 이미지 경로 가정

function LoginPage() {
  return (
    <div className="loginPage">
      <img src={logoImage} alt="Logo" className="logo"/>
      <div className="loginBox">
        <h2>로그인</h2>
        <div className="inputGroup">
          <label htmlFor="email">이메일 입력</label>
          <input type="email" id="email" placeholder="이메일을 입력하세요"/>
        </div>
        <div className="inputGroup">
          <label htmlFor="password">비밀번호 입력</label>
          <input type="password" id="password" placeholder="비밀번호를 입력하세요"/>
        </div>
        <button className="loginButton">로그인하기</button>
        <Link to="/signin" className="signupLink">아직 MOVIELY 계정이 없다면? 회원가입하기</Link>
      </div>
    </div>
  );
}

export default LoginPage;
