// Popcal.js
import React, { useState, useEffect } from 'react';
import '../css/Popcal.css';

const Popcal = ({ isOpen, onClose, onSave, user, initialData }) => {
  // 초기 데이터 또는 기본값으로 상태를 설정
  const [movieData, setMovieData] = useState({
    watch_date: '',
    movie_title: '',
    movie_content: '',
    user_id: '',
    created: '',
    created_by: ''
  });

  // initialData가 변경될 때마다 컴포넌트의 상태를 업데이트
  useEffect(() => {
    if (initialData) {
      setMovieData({
        watch_date: initialData.start || '', // 영화 관람 날짜 설정
        movie_title: initialData.title || '', // 영화 제목 설정
        movie_content: initialData.movie_content || '', // 영화 내용 설정
        user_id: user?.id || '',
        created: initialData.created || '',
        created_by: user?.name || ''
      });
    }
  }, [initialData, user]);
  // 입력 필드 변경을 처리하는 함수
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setMovieData(prevData => ({ ...prevData, [name]: value }));
  };

  // 저장 버튼 핸들러
  const handleSave = () => {
    onSave(movieData);
    onClose(); // 저장 후 팝업 닫기
  };

  if (!isOpen) return null;

  return (
    <div className="popcal-container">
      <div className="popcal">
        <h2>영화 관람 기록하기</h2>
        <label htmlFor="watch_date">관람 일자</label>
        <input
          id="watch_date"
          type="date"
          name="watch_date"
          value={movieData.watch_date}
          onChange={handleInputChange}
        />
        <label htmlFor="movie_title">영화 제목</label>
        <input
          id="movie_title"
          type="text"
          name="movie_title"
          placeholder="영화 제목"
          value={movieData.movie_title}
          onChange={handleInputChange}
        />
        <label htmlFor="movie_content">감상 기록</label>
        <textarea
          id="movie_content"
          name="movie_content"
          placeholder="감상 기록"
          value={movieData.movie_content}
          onChange={handleInputChange}
        />
        <div className="popcal-buttons">
          <button onClick={onClose}>닫기</button>
          <button onClick={handleSave}>저장하기</button>
        </div>
      </div>
    </div>
  );
};

export default Popcal;
