import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import LandingPage from './pages/LandingPage';
import LoginPage from './pages/LoginPage';
import SigninPage from './pages/SigninPage';
import MvchoPage from './pages/MvchoPage';
import RecomPage from './pages/RecomPage';
import MvdetailPage from './pages/MvdetailPage';
import MyalrPage from './pages/MyalrPage';
import MywishPage from './pages/MywishPage';
import MycalPage from './pages/MycalPage';
import MvsrchPage from './pages/MvsrchPage';

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setIsAuthenticated(true); // 토큰이 있으면 인증된 상태로 설정
    }
  }, []);

  return (
    <Router>
      <Routes>
        <Route path="/" element={<LandingPage />} />
        <Route path="/login" element={<LoginPage />} />
        <Route path="/signin" element={<SigninPage />} />
        <Route path="/movie-select" element={<MvchoPage />} />
        <Route path="/recommendations" element={isAuthenticated ? <RecomPage /> : <Navigate to="/login" />} />
        <Route path="/movie/:id" element={isAuthenticated ? <MvdetailPage /> : <Navigate to="/login" />} />
        <Route path="/my/watched" element={isAuthenticated ? <MyalrPage /> : <Navigate to="/login" />} />
        <Route path="/my/wishlist" element={isAuthenticated ? <MywishPage /> : <Navigate to="/login" />} />
        <Route path="/my/calendar" element={isAuthenticated ? <MycalPage /> : <Navigate to="/login" />} />
        <Route path="/movie-search" element={isAuthenticated ? <MvsrchPage /> : <Navigate to="/login" />} />
      </Routes>
    </Router>
  );
};

export default App;
