// MycalPage.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import interactionPlugin from '@fullcalendar/interaction';
import Popcal from './Popcal';
import '../css/MycalPage.css';
import logoImage from '../logo.png';

function MycalPage() {
  const [isPopupOpen, setIsPopupOpen] = useState(false); // 팝업 상태 관리
  const [selectedDate, setSelectedDate] = useState(null); // 선택된 날짜 관리
  const [selectedEvent, setSelectedEvent] = useState(null); // 선택된 이벤트(영화 정보) 관리
  const [events, setEvents] = useState([]); // 달력에 표시될 이벤트들 관리

  // 달력의 날짜를 클릭했을 때의 처리
  const handleDateClick = ({ dateStr }) => {
    setSelectedDate(dateStr);
    setSelectedEvent(null); // 새로운 기록을 위해 선택된 이벤트 초기화
    setIsPopupOpen(true);
  };

  // '영화 관람 기록하기' 버튼 클릭 처리
  const handleRecordButtonClick = () => {
    setSelectedDate(new Date().toISOString().split('T')[0]); // 오늘 날짜를 선택
    setSelectedEvent(null); // 기존 선택된 이벤트 초기화
    setIsPopupOpen(true); // 팝업창 열기
  };

  // 팝업 닫기 처리
  const handleClosePopup = () => {
    setIsPopupOpen(false);
  };

  // 영화 데이터 저장 처리
  const handleSaveMovieData = (movieData) => {
    if (selectedEvent) {
      // 이벤트 수정 로직
      const updatedEvents = events.map(event => 
        event.id === selectedEvent.id ? { ...event, title: movieData.movie_title } : event
      );
      setEvents(updatedEvents);
    } else {
      // 새 이벤트 추가 로직
      setEvents([...events, {
        id: Date.now(), // unique id for the new event
        title: movieData.movie_title,
        start: selectedDate,
        allDay: true
      }]);
    }
    setIsPopupOpen(false);
  };

  // 달력의 이벤트(영화 제목)을 클릭했을 때의 처리
  const handleEventClick = ({ event }) => {
    setSelectedDate(event.startStr);
    setSelectedEvent(event); // 수정할 이벤트 설정
    setIsPopupOpen(true);
  };

  return (
    <div className="mycalPage">
      <img src={logoImage} alt="Logo" className="myPageLogo" />
      <div className="myPageTitle">마이페이지</div>
      <div className="navButtons">
        <Link to="/my/watched" className="navButton">이미 본 영화</Link>
        <Link to="/my/wishlist" className="navButton">보고싶은 영화</Link>
        <Link to="/my/calendar" className="navButton active">MOVIELY 캘린더</Link>
      </div>
      <button onClick={handleRecordButtonClick} className="recordButton">
        영화 관람 기록하기
      </button>
      <div className="calendar-container">
        <FullCalendar
          plugins={[dayGridPlugin, interactionPlugin]}
          initialView="dayGridMonth"
          dateClick={handleDateClick}
          eventClick={handleEventClick}
          events={events}
          height="470px"
        />
      </div>
      {isPopupOpen && (
        <Popcal
          isOpen={isPopupOpen}
          onClose={handleClosePopup}
          onSave={handleSaveMovieData}
          initialData={selectedEvent ? { 
            movie_title: selectedEvent.title, 
            movie_date: selectedEvent.start 
          } : null} // 선택된 이벤트의 데이터를 전달
        />
      )}
    </div>
  );
}

export default MycalPage;
