import React from 'react';
import { useParams } from 'react-router-dom';
import { mockMovies } from './Mockmv';
import '../css/MvdetailPage.css'; // CSS 파일 경로 확인

function MovieDetailPage() {
  const { id } = useParams(); // URL에서 id 값을 가져옴
  const movieId = parseInt(id, 10); // id 값을 정수로 변환

  // 목업 데이터에서 해당 id를 가진 영화를 찾음
  const movie = mockMovies.find(m => m.movie_id === movieId);

  if (!movie) {
    return <div>Movie not found.</div>; // 영화를 찾지 못했을 때의 메시지
  }

  return (
    <div className="container">
      <img src={movie.poster_path} alt="Logo" className="logo" />
      <button className="my-page-button">마이 페이지</button>
      <input type="text" className="search-bar" placeholder="Search..." />
      <div className="section">
        <div className="details">
          <h1>{movie.title}</h1>
          {/* Other details here */}
        </div>
        <img src={movie.poster_path} alt={movie.title} className="poster"/>
      </div>
      <div className="section">
        <p>{movie.overview}</p>
      </div>
      <div className="section">
        {/* OTT buttons */}
      </div>
    </div>
  );
}

export default MovieDetailPage;
