import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../css/SigninPage.css';
import logoImage from '../logo.png';

function SigninPage() {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [gender, setGender] = useState('');
  const [age, setAge] = useState('');

  const handleSignup = async () => {
    try {
      const response = await fetch('http://43.203.39.119:8080/signup', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password, gender, age }),
      });

      if (response.ok) {
        navigate('/movie-select');
      } else {
        alert('회원가입 실패');
      }
    } catch (error) {
      console.error('Error:', error);
      alert('서버와의 연결에 문제가 발생했습니다.');
    }
  };

  const ageOptions = [];
  for (let i = 15; i <= 100; i++) {
    ageOptions.push(<option key={i} value={i}>{i}</option>);
  }

  return (
    <div className="signinPage">
      <img src={logoImage} alt="Logo" className="logo" />
      <div className="signinBox">
        <h2>회원가입</h2>
        <div className="inputGroup">
          <label htmlFor="email">이메일 입력</label>
          <input
            type="email"
            id="email"
            name="user_email"
            placeholder="이메일을 입력하세요"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div className="inputGroup">
          <label htmlFor="password">비밀번호 입력</label>
          <input
            type="password"
            id="password"
            name="user_pwd"
            placeholder="비밀번호를 입력하세요"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <div className="selectGroup">
          <div>
            <label htmlFor="gender">성별</label>
            <select
              id="gender"
              name="gender"
              className="genderSelect"
              value={gender}
              onChange={(e) => setGender(e.target.value)}
            >
              <option value="" disabled>성별을 선택해주세요</option>
              <option value="female">여성</option>
              <option value="male">남성</option>
            </select>
          </div>
          <div>
            <label htmlFor="age">나이</label>
            <select
              id="age"
              name="age"
              className="ageSelect"
              value={age}
              onChange={(e) => setAge(e.target.value)}
            >
              <option value="" disabled>나이를 선택해주세요</option>
              {ageOptions}
            </select>
          </div>
        </div>
        <button onClick={handleSignup} className="signinButton">가입하기</button>
        <Link to="/login" className="loginLink">이미 MOVIELY 계정이 있으신가요?</Link>
      </div>
    </div>
  );
}

export default SigninPage;
