import React from 'react';
import { useParams } from 'react-router-dom';
import { mockMovies } from './Mockmv';
import '../css/MvdetailPage.css';

function MovieDetailPage() {
  const { id } = useParams();
  const movieId = parseInt(id, 10);
  const movie = mockMovies.find(m => m.movie_id === movieId);

  if (!movie) {
    return <div>Movie not found.</div>;
  }

  // 출연진 정보를 문자열로 연결
  const actorsList = movie.actors.join(", ");

  return (
    <div className="container">
      <div className="section">
        <div className="details" style={{ display: 'flex' }}>
          <div style={{ flex: 1, color: 'white' }}>
            <h1>{movie.title}</h1>
            <div>
              <div>별점: {movie.vote_average} ({movie.vote_count} votes)</div>
              <button>Add to My Page</button>
            </div>
            <div>개봉일: {movie.release_date}</div>
            <div>장르: {movie.genre}</div>
            <div>제작 국가: {movie.production_countries}</div>
            <div>러닝타임: {movie.run_time} minutes</div>
            <div>출연진: {actorsList}</div>
          </div>
          <img src={movie.poster_path} alt={movie.title} className="poster" style={{ width: '50%' }}/>
        </div>
      </div>
      <div className="section">
        <p style={{ color: 'white' }}>{movie.overview}</p>
      </div>
      <div className="section">
        <div className="ott-links">
          <h2 className="ott-heading">바로가기</h2>
          {movie.flatrate.map(service => (
            <a href={service.link} key={service.name} target="_blank" rel="noopener noreferrer">
              <button className="ott-button">{service.name}</button>
            </a>
          ))}
        </div>
      </div>
    </div>
  );
}

export default MovieDetailPage;
