import React, { useState, useEffect, useMemo } from 'react';
import MvBanner from './MvBanner';
import '../css/MvsrchPage.css';
import logoImage from '../logo.png';
import watchaLogo from '../watcha.png';
import netflixLogo from '../netflix.png';
import disneyPlusLogo from '../disneyplus.png';
import wavveLogo from '../wavve.png';

function MvsrchPage() {
  const genreMapping = useMemo(() => ({
    '장르 전체': 'All',
    '액션': '28',
    '모험': '12',
    '애니메이션': '16',
    '코미디': '35',
    '범죄': '80',
    '다큐멘터리': '99',
    '드라마': '18',
    '가족': '10751',
    '판타지': '14',
    '역사': '36',
    '공포': '27',
    '음악': '10402',
    '미스터리': '9648',
    '로맨스': '10749',
    'SF': '878',
    'TV 영화': '10770',
    '스릴러': '53',
    '전쟁': '10752',
    '서부': '37'
  }), []);

  const genres = useMemo(() => Object.keys(genreMapping), [genreMapping]);
  const [selectedGenre, setSelectedGenre] = useState('장르 전체');
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchMovies = async () => {
      try {
        console.log('Fetching movies...');
        const response = await fetch('http://43.203.39.119:8080/api/movies?size=1000', { mode: 'cors' });
        console.log('API 요청 성공:', response);

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const data = await response.json();
        console.log('API 응답 데이터:', data);

        if (data && data.content) {
          const processedData = data.content.map(movie => ({
            ...movie,
            flatrate: movie.flatrate ? movie.flatrate.split(', ') : [],
            genre: movie.genre ? movie.genre.split(', ') : [] // 장르 필드를 배열로 변환
          }));
          setMovies(processedData);
          console.log('Processed Data:', processedData); 
        } else {
          console.error('Unexpected response format:', data);
        }

        setLoading(false);
      } catch (error) {
        console.error('Error fetching movies:', error);
        setLoading(false);
      }
    };

    fetchMovies();
  }, []);

  const filteredMovies = selectedGenre === '장르 전체' ? movies : movies.filter(movie =>
    movie.genre.includes(genreMapping[selectedGenre])
  );

  const banners = filteredMovies.map((movie, index) => (
    <MvBanner
      key={index}
      title={movie.title}
      poster={movie.poster_path}
      flatrate={movie.flatrate.join(', ')} 
      rating={Math.round(movie.vote_average / 2)}
    />
  ));

  let resultText = '';
  if (selectedPlatform !== '전체') {
    resultText += `플랫폼 : <span style="color: yellow;">${selectedPlatform}</span>`;
  }
  if (selectedGenre !== '전체') {
    if (resultText) {
      resultText += ', ';
    }
    resultText += `장르 : <span style="color: yellow;">${selectedGenre}</span>`;
  }
  if (resultText) {
    resultText += ' 검색 결과입니다.';
  }

  return (
    <div className="MvsrchPage">
      <img src={logoImage} alt="Logo" className="logo"/>
      <div className="searchContainer">
        <div className="dropdownContainer">
          <button onClick={() => setShowPlatforms(!showPlatforms)} className="platformButton">플랫폼ㅤㅤㅤ▼</button>
          {showPlatforms && (
            <div className="platformDropdown">
              {platforms.map((platform) => (
                <button
                  key={platform.name}
                  onClick={() => { handlePlatformClick(platform.name); setShowPlatforms(false); }}
                  className="filter"
                >
                  {platform.logo && <img src={platform.logo} alt={platform.name} className="logoIcon"/>}
                  {platform.name}
                </button>
              ))}
            </div>
          )}
        </div>
        <div className="dropdownContainer">
          <button onClick={() => setShowGenres(!showGenres)} className="genreButton">장르ㅤㅤㅤㅤ▼</button>
          {showGenres && (
            <div className="genreDropdown">
              {genres.map((genre) => (
                <button
                  key={genre}
                  onClick={() => { handleGenreClick(genre); setShowGenres(false); }}
                  className="filter"
                >
                  {genre}
                </button>
              ))}
            </div>
          )}
        </div>
        <input
          type="text"
          className="searchInput"
          placeholder="검색"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && handleSearchClick()} // Enter key trigger search
        />
        <button className="searchButton" onClick={handleSearchClick}>검색</button>
      </div>
      <div className="resultText" style={{ marginTop: '20px' }}>
        <span dangerouslySetInnerHTML={{ __html: resultText }} />
      </div>
      <div className="movieResults" style={{ marginTop: '30px' }}>
        {loading ? (
          <div>Loading...</div>
        ) : (
          filteredMovies.length > 0 ? (
            <div className="movieGrid">
              {filteredMovies.map((movie, index) => (
                <MvBanner
                  key={index}
                  title={movie.title}
                  poster={movie.poster_path}
                  flatrate={movie.flatrate.join(', ')}
                  rating={Math.round(movie.vote_average / 2)}
                />
              ))}
            </div>
          ) : (
            <div className="noMovies">검색 결과가 없습니다.</div>
          )
        )}
      </div>
    </div>
  );
}

export default MvsrchPage;
