import React, { useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import '../css/LogSignPage.css';
import logoImage from '../logo.png';

function LogSignPage() {
  const navigate = useNavigate();

  const handleGoogleLogin = () => {
    // 실제 백엔드의 구글 로그인 요청 URL로 리디렉션
    window.location.href = 'http://moviely.duckdns.org/oauth2/authorize/google';
  };

  useEffect(() => {
    // URL에 code가 있는지 확인하여, 백엔드에서 리디렉션된 경우 추가 정보 입력 페이지로 이동
    const urlParams = new URLSearchParams(window.location.search);
    const code = urlParams.get('code');
    const error = urlParams.get('error');

    if (code) {
      // 백엔드에서 로그인 처리가 완료된 후 리디렉션된 경우 추가 처리
      handleBackendRedirection(code);
    } else if (error) {
      alert('Google 로그인에 실패했습니다.');
    }
  }, []);

  const handleBackendRedirection = async (code) => {
    try {
      // 백엔드로 code를 보내서 추가 처리 (토큰 교환 등) 후 리디렉션
      const response = await fetch(`https://moviely.duckdns.org/api/login?code=${code}`);
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();

      if (data.isNewUser) {
        navigate('/add');
      } else {
        navigate('/movie-select');
      }
    } catch (error) {
      console.error('Backend redirection failed:', error);
      alert('백엔드 처리에 실패했습니다.');
    }
  };

  return (
    <div className="logSignPage">
      <Link to="/" className="logSign-logo">
        <img src={logoImage} alt="Logo" />
      </Link>
      <div className="logSignBox">
        <h2>로그인 및 회원가입</h2>
        <p>소셜 로그인 및 회원가입으로<br />MOVIELY의 모든 서비스를 이용하실 수 있습니다.</p>
        <button className="googleLoginButton" onClick={handleGoogleLogin}>
          Google로 시작하기
        </button>
      </div>
    </div>
  );
}

export default LogSignPage;
