// MvBanner.js
import React, { useState } from 'react';
import '../css/Mvbanner.css'; // CSS 파일 임포트
import Popcho from '../pages/Popcho'; // Popcho 모달 컴포넌트 임포트
import watchaLogo from '../watcha.png'; // Watcha 로고 이미지
import netflixLogo from '../netflix.png'; // Netflix 로고 이미지
import disneyPlusLogo from '../disneyplus.png'; // Disney Plus 로고 이미지

// flatrate 값을 로고 이미지로 매핑합니다.
const flatrateLogos = {
  'Watcha': watchaLogo,
  'Netflix': netflixLogo,
  'Disney Plus': disneyPlusLogo,
};

const MvBanner = ({ title, poster, flatrate }) => {
  const [rating, setRating] = useState(0); // 별점 상태 초기화
  const [showModal, setShowModal] = useState(false); // 모달창 보임 상태 초기화

  const handleStarClick = (index) => {
    // 별점 클릭 핸들러
    setRating(rating === index + 1 ? rating - 1 : index + 1);
  };

  const handleAddClick = () => {
    // 추가하기 버튼 클릭 핸들러
    setShowModal(true);
  };

  const handleCloseModal = () => {
    // 모달 닫기 핸들러
    setShowModal(false);
  };

  const handleSaveModal = (option) => {
    // 모달 저장 핸들러
    console.log("저장 옵션:", option);
    setShowModal(false);
  };

  const logoImage = flatrateLogos[flatrate]; // flatrate를 사용하여 로고 이미지 경로를 결정합니다.

  return (
    <div className="movie-banner">
      <img src={poster} alt={title} className="movie-poster" />
      <div className="movie-info">
        {logoImage && (
          <img src={logoImage} alt={`${flatrate} 로고`} className="flatrate-logo" />
        )}
        <div className="movie-title">{title}</div>
        <div className="movie-rating">
          {[...Array(5)].map((_, index) => (
            <span key={index} 
              className={`star ${rating > index ? 'filled' : ''}`}
              onClick={() => handleStarClick(index)}>
              ★
            </span>
          ))}
        </div>
        <button onClick={handleAddClick} className="add-button">+</button>
      </div>
      {showModal && <Popcho onClose={handleCloseModal} onSave={handleSaveModal} />}
    </div>
  );
};

export default MvBanner;
