import React from 'react';
import { Link } from 'react-router-dom';
import '../MvchoPage.css'; // 스타일시트 임포트


function MvchoPage() {
  // 장르 목록
  const genres = ['장르 전체', '스릴러', 'SF', '미스터리', '애니메이션', '로맨스', '판타지', '다큐멘터리', '코미디', '공포'];

  return (
    <div className="MvchoPage">
      <img src="../logo.png" alt="Logo" className="logo" />
      <div className="mainText">재미있게 봤거나 눈길이 가는 영화들을 평가해주세요.</div>
      <div className="subText">찜한 영화들을 바탕으로 MOVIELY가 취향저격 영화들을 추천해 드려요.</div>
      <Link to="/recommendations">
        <button className="recommendButton">영화 추천 받기 &gt;</button>
      </Link>
      <div className="genreButtons">
        {genres.map((genre) => (
          <button key={genre} className="genreButton">{genre}</button>
        ))}
      </div>
      {/* 추가: 마이페이지(이미 본 영화)로 이동하는 버튼 */}
      <Link to="/my/watched">
        <button className="myPageButton">마이페이지</button>
      </Link>
    </div>
  );
}

export default MvchoPage;
