import React, { useState } from 'react';
import './Popcho.css'; // 수정된 CSS 경로가 올바른지 확인하세요.

function Popcho({ onClose, onSave }) {
  const [selectedOption, setSelectedOption] = useState('');

  return (
    <div className="modalBackground" onClick={onClose}>
      <div className="modalContainer" onClick={e => e.stopPropagation()}>
        <div className="modalHeader">내 마이페이지에 저장하기</div>
        <form className="radioForm">
          <label className="radioLabel">
            <input
              type="radio"
              value="seen"
              checked={selectedOption === 'seen'}
              onChange={() => setSelectedOption('seen')}
            />
            이미 본 영화
          </label>
          <label className="radioLabel">
            <input
              type="radio"
              value="toWatch"
              checked={selectedOption === 'toWatch'}
              onChange={() => setSelectedOption('toWatch')}
            />
            보고 싶은 영화
          </label>
        </form>
        <div className="modalButtons">
          <button onClick={onClose} className="closeButton">닫기</button>
          <button onClick={() => onSave(selectedOption)} className="saveButton">저장</button>
        </div>
      </div>
    </div>
  );
}

export default Popcho;
