import React, { useEffect, useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { gapi } from 'gapi-script';
import '../css/LogSignPage.css';
import logoImage from '../logo.png';
import { useAuth } from '../context/AuthContext';

const CLIENT_ID = '13094654813-nqglekemv1iff66rsq4luqftbfgv51hi.apps.googleusercontent.com';

function LogSignPage() {
  const navigate = useNavigate();
  const { login } = useAuth();
  const [gapiLoaded, setGapiLoaded] = useState(false);

  useEffect(() => {
    const initClient = () => {
      gapi.client.init({
        clientId: CLIENT_ID,
        scope: '',
      }).then(() => {
        setGapiLoaded(true);
      }).catch((error) => {
        console.error('Error initializing gapi client', error);
      });
    };

    gapi.load('client:auth2', initClient);
  }, []);

  const handleGoogleLogin = () => {
    if (!gapiLoaded) {
      alert('Google API is not loaded yet. Please try again.');
      return;
    }

    const auth2 = gapi.auth2.getAuthInstance();
    if (!auth2) {
      alert('Failed to initialize Google Auth instance.');
      return;
    }

    auth2.signIn().then(async (googleUser) => {
      const idToken = googleUser.getAuthResponse().id_token;

      try {
        const res = await fetch('https://moviely.duckdns.org/api/login', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ token: idToken }), 
        });
        const data = await res.json();

        if (data.token) {
          login(data.token, data.user);
          if (data.isNewUser) {
            navigate('/add');
          } else {
            navigate('/movie-select');
          }
        } else {
          alert('로그인에 실패했습니다.');
        }
      } catch (error) {
        console.error('Backend redirection failed:', error);
        alert('백엔드 처리에 실패했습니다.');
      }
    }).catch((error) => {
      console.error('Google Login Failed:', error);
      alert('Google 로그인에 실패했습니다.');
    });
  };

  return (
    <div className="logSignPage">
      <Link to="/" className="logSign-logo">
        <img src={logoImage} alt="Logo" />
      </Link>
      <div className="logSignBox">
        <h2>로그인 및 회원가입</h2>
        <p>소셜 로그인 및 회원가입으로<br />MOVIELY의 모든 서비스를 이용하실 수 있습니다.</p>
        <button className="googleLoginButton" onClick={handleGoogleLogin}>
          Google로 시작하기
        </button>
      </div>
    </div>
  );
}

export default LogSignPage;
