import React from 'react';
import { Link } from 'react-router-dom';

function LandingPage() {
  // 버튼과 컨테이너 스타일 정의
  const buttonStyle = {
    width: '1000px',
    height: '100px',
    margin: '10px',
  };

  const containerStyle = {
    display: 'flex',
    flexDirection: 'row', // 기본 방향은 가로
    flexWrap: 'wrap', // 화면이 줄어들면 아래로 내려가게 설정
    justifyContent: 'center', // 버튼들을 중앙에 배치
    alignItems: 'center', // 버튼들을 세로 방향 중앙에 배치
    height: '100vh', // 뷰포트 높이의 100%를 차지
  };

  return (
    <div style={containerStyle}>
      <h1>랜딩 페이지</h1>
      {/* 로그인 페이지로 이동하는 버튼 */}
      <Link to="/login"><button style={buttonStyle}>로그인</button></Link>
      {/* 회원가입 페이지로 이동하는 버튼 */}
      <Link to="/signin"><button style={buttonStyle}>회원가입</button></Link>
    </div>
  );
}

export default LandingPage;
