import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import FullCalendar from '@fullcalendar/react';
import dayGridPlugin from '@fullcalendar/daygrid';
import interactionPlugin from '@fullcalendar/interaction';
import Popcal from './Popcal';
import '../css/MycalPage.css';
import logoImage from '../logo.png';

function MycalPage() {
  // 팝업의 표시 상태와 이벤트 데이터 상태를 관리합니다.
  const [isPopupOpen, setIsPopupOpen] = useState(false);
  const [eventData, setEventData] = useState({
    watch_date: '', // 관람 일자
    movie_title: '', // 영화 제목
    movie_content: '' // 감상 기록
  });

  // 날짜 클릭 이벤트 핸들러
  const handleDateClick = ({ dateStr }) => {
    // 클릭된 날짜를 eventData 상태에 설정합니다.
    setEventData({ ...eventData, watch_date: dateStr });
    // 팝업창을 엽니다.
    setIsPopupOpen(true);
  };

  // 기록하기 버튼 클릭 핸들러
  const handleRecordButtonClick = () => {
    // 현재 날짜를 관람 일자로 설정합니다.
    setEventData({ ...eventData, watch_date: new Date().toISOString().split('T')[0] });
    // 팝업창을 엽니다.
    setIsPopupOpen(true);
  };

  // 팝업창을 닫는 핸들러
  const handleClosePopup = () => {
    setIsPopupOpen(false);
  };

  // 데이터를 저장하는 핸들러
  const handleSave = () => {
    console.log('저장할 데이터:', eventData);
    // TODO: 여기에 백엔드로 데이터를 저장하는 코드를 구현하세요.
    // 팝업창을 닫습니다.
    setIsPopupOpen(false);
  };

  return (
    <div className="mycalPage">
      <img src={logoImage} alt="Logo" className="myPageLogo" />
      <div className="myPageTitle">마이페이지</div>
      <div className="navButtons">
        <Link to="/my/watched" className="navButton">이미 본 영화</Link>
        <Link to="/my/wishlist" className="navButton">보고싶은 영화</Link>
        <Link to="/my/calendar" className="navButton active">MOVIELY 캘린더</Link>
      </div>
      <button onClick={handleRecordButtonClick} className="recordButton">
        영화 관람 기록하기
      </button>
      <div className="calendar-container">
        <FullCalendar
          plugins={[dayGridPlugin, interactionPlugin]}
          initialView="dayGridMonth"
          dateClick={handleDateClick}
          height="470px"
        />
      </div>
      <Popcal
        isOpen={isPopupOpen}
        onClose={handleClosePopup}
        onSave={handleSave}
        eventData={eventData}
        setEventData={setEventData}
      />
    </div>
  );
}

export default MycalPage;
